css:
.editor .marker .region {
  position: absolute;
  pointer-events: none;
  z-index: -1;
}
.editor .search-results .marker .region,
.editor .find-result .region {
  background-color: transparent;
  border-radius: 2px;
  border: 1px solid #969896;
}
.editor .search-results .marker.current-result .region,
.editor .current-result .region {
  border-radius: 2px;
  border: 1px solid #ffffff;
}
.editor .find-result,
.editor .current-result {
  display: none;
}
.workspace.find-visible .editor .find-result,
.workspace.find-visible .editor .current-result {
  display: block;
}
.find-and-replace .options-label,
.project-find .options-label {
  color: #555555;
  position: relative;
}
.find-and-replace .options-label .options,
.project-find .options-label .options {
  color: #aaaaaa;
}
.find-and-replace .subtle-info-message,
.project-find .subtle-info-message {
  padding-left: 5px;
  color: #555555;
}
.find-and-replace .subtle-info-message .highlight,
.project-find .subtle-info-message .highlight {
  color: #aaaaaa;
  font-weight: normal;
}
.find-and-replace .find-container,
.project-find .find-container,
.find-and-replace .replace-container,
.project-find .replace-container,
.find-and-replace .paths-container,
.project-find .paths-container {
  display: -webkit-flex;
  -webkit-flex-direction: row;
}
.find-and-replace .find-container .btn-group,
.project-find .find-container .btn-group,
.find-and-replace .replace-container .btn-group,
.project-find .replace-container .btn-group,
.find-and-replace .paths-container .btn-group,
.project-find .paths-container .btn-group {
  margin-left: 10px;
}
.find-and-replace .find-container .editor-container,
.project-find .find-container .editor-container,
.find-and-replace .replace-container .editor-container,
.project-find .replace-container .editor-container,
.find-and-replace .paths-container .editor-container,
.project-find .paths-container .editor-container {
  position: relative;
  -webkit-flex: 1;
}
.find-and-replace .find-container .editor-container .editor,
.project-find .find-container .editor-container .editor,
.find-and-replace .replace-container .editor-container .editor,
.project-find .replace-container .editor-container .editor,
.find-and-replace .paths-container .editor-container .editor,
.project-find .paths-container .editor-container .editor {
  width: 100%;
}
.find-and-replace {
  padding: 10px;
  -webkit-user-select: none;
}
.find-and-replace .btn-group-replace.btn-group {
  width: 200px;
}
.find-and-replace .btn-group-replace.btn-group .btn {
  width: 50%;
}
.find-and-replace .btn-group-replace-all.btn-group {
  width: 100px;
}
.find-and-replace .btn-group-replace-all.btn-group .btn {
  width: 100%;
}
.find-and-replace .btn-group-find.btn-group {
  width: 200px;
}
.find-and-replace .btn-group-find.btn-group .btn {
  width: 50%;
}
.find-and-replace .btn-group-options.btn-group {
  width: 100px;
}
.find-and-replace .btn-group-options.btn-group .btn {
  font-size: 14px;
  font-family: courier, monospace;
  width: 33.33%;
}
.find-and-replace .btn-group-options.btn-group .btn.option-selection {
  font-family: "Times New Roman";
  font-size: 20px;
  padding-top: 3px;
}
.find-and-replace .find-meta-container {
  position: absolute;
  top: 1px;
  right: 0;
  z-index: 2;
  font-size: .9em;
  line-height: 25px;
}
.find-and-replace .find-meta-container .result-counter {
  margin-right: 10px;
}
.project-find {
  -webkit-user-select: none;
}
.project-find .loading,
.project-find .preview-block,
.project-find .error-messages,
.project-find .filter-container {
  display: none;
}
.project-find .btn-group.btn-group-options {
  width: 90px;
}
.project-find .btn-group.btn-group-options .btn {
  font-size: 14px;
  font-family: courier, monospace;
  width: 50%;
}
.project-find .btn-group.btn-group-replace-all {
  width: 90px;
}
.project-find .btn-group.btn-group-replace-all .btn {
  width: 100%;
}
.preview-pane {
  position: relative;
  display: -webkit-flex;
  -webkit-flex-direction: column;
}
.preview-pane .panel-heading {
  -webkit-flex-shrink: 0;
}
.preview-pane .no-results-overlay {
  visibility: hidden;
}
.preview-pane.no-results .no-results-overlay {
  visibility: visible;
}
.preview-pane .results-view {
  overflow: auto;
  position: relative;
  padding: 0 10px;
  -webkit-flex: 1;
}
.preview-pane .results-view .search-result {
  padding-top: 2px;
  padding-bottom: 2px;
}
.preview-pane .results-view .line-number {
  margin-right: 1ex;
  text-align: right;
  display: inline-block;
}
.preview-pane .results-view .selected .line-number {
  color: #ffffff;
}
.preview-pane .results-view .path-match-number {
  padding-left: 10px;
  color: #555555;
}
.preview-pane .results-view .preview {
  word-break: break-all;
  font-family: monospace;
}
.preview-pane .results-view .selected .highlight-info {
  box-shadow: inset 0 0 1px #ffffff;
}
.preview-pane .results-view .selected .highlight-error {
  box-shadow: inset 0 0 1px #ff4d4d;
}
.preview-pane .results-view .selected .highlight-success {
  box-shadow: inset 0 0 1px #72efa8;
}
